package controlstatement;

public class SwitchDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int number =30;
		switch(number)
		{
		
		    case 10:
			System.out.println("Ten");
			break; 
			
			
		       case 20:
				System.out.println("Twenty");
				break;
				
				
				default:
					System.out.println("Not matching");
		
		} 

	}

}
