package wrapperpack;

public class Unboxing {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Integer a=new Integer(3);
		
		
		int i=a.intValue();
		
		int j=a;// unboxing
		
		System.out.println(a);
	
	
		System.out.println(i);
		
		
		System.out.println(j);
		
	}

}
